public class CustomClientBase<T> : ClientBase<T> where T : class\n{\n    public CustomClientBase()\n    {\n        // Configure the endpoint address and binding\n        EndpointAddress address = new EndpointAddress("http://localhost:8080/RemoteCommandService");\n        BasicHttpBinding binding = new BasicHttpBinding();\n\n        // Create a new channel factory\n        ChannelFactory<T> factory = new ChannelFactory<T>(binding, address);\n\n        // Create a new channel\n        T channel = factory.CreateChannel();\n\n        // Set the channel as the inner channel\n        InnerChannel = channel;\n    }\n}