using Microsoft.AspNet.Identity;\nusing Microsoft.AspNet.Identity.EntityFramework;\n\n// Create a new user\nvar userManager = new UserManager<ApplicationUser>(new UserStore<ApplicationUser>(new ApplicationDbContext()));\nvar user = new ApplicationUser { UserName = "john.doe@example.com", Email = "john.doe@example.com" };\nvar result = userManager.Create(user, "password123");\n\n// Authenticate a user\nvar signInManager = new SignInManager<ApplicationUser, string>(userManager, HttpContext.Current.GetOwinContext().Authentication);\nvar signInResult = signInManager.PasswordSignIn(user.UserName, "password123", isPersistent: false, shouldLockout: false);