using (var dbContext = new MyDataContext())\n{\n    var entity = dbContext.MyEntities.First(e => e.Id == 1);\n    // make some changes to entity\n    var originalValues = dbContext.GetChangeSet().GetOriginalValues(entity);\n    // call your stored procedure, passing both new and original values\n    dbContext.MyUpdateProcedure(entity.Property1, entity.Property2, originalValues.Property1, originalValues.Property2);\n}