public class CoordinateScaleTransform : Transform\n{\n    private double _scaleX;\n    private double _scaleY;\n\n    public CoordinateScaleTransform(double scaleX, double scaleY)\n    {\n        _scaleX = scaleX;\n        _scaleY = scaleY;\n    }\n\n    public override Matrix Value\n    {\n        get\n        {\n            var matrix = new Matrix(_scaleX, 0, 0, _scaleY, 0, 0);\n            return matrix;\n        }\n    }\n}