using System;\nusing System.Drawing;\nusing System.Drawing.Text;\nusing System.Runtime.InteropServices;\n\nclass CustomFontLoader\n{\n    [DllImport("gdi32.dll")]\n    private static extern int AddFontResourceEx(string lpszFilename, int fl, IntPtr pdv);\n\n    private const int FR_PRIVATE = 0x10;\n\n    public static void LoadCustomFont(string fontPath)\n    {\n        // Load the font into the font collection\n        PrivateFontCollection privateFonts = new PrivateFontCollection();\n        privateFonts.AddFontFile(fontPath);\n\n        // Add the font resource for GDI usage\n        AddFontResourceEx(fontPath, FR_PRIVATE, IntPtr.Zero);\n    }\n\n    public static void Main()\n    {\n        string fontPath = @"C:\Path\To\Your\CustomFont.ttf";\n        LoadCustomFont(fontPath);\n\n        // Example usage with GDI+\n        using (var font = new Font("CustomFontName", 12f))\n        {\n            // Use your font here\n        }\n    }\n}