DirectoryEntry directoryEntry = new DirectoryEntry("LDAP://yourdomaincontroller.com/DC=yourdomain,DC=com");\nDirectorySearcher searcher = new DirectorySearcher(directoryEntry);\nsearcher.Filter = "(objectClass=group)";\nsearcher.PropertiesToLoad.Add("cn");\n\nSearchResultCollection results = searcher.FindAll();\n\nforeach (SearchResult result in results)\n{\n    if (result.Properties["cn"][0].ToString() == "Administrators")\n    {\n        // Get the members of the Administrators group\n        DirectoryEntry groupEntry = new DirectoryEntry(result.Path);\n        object members = groupEntry.Invoke("members", null);\n\n        foreach (object groupMember in (IEnumerable)members)\n        {\n            DirectoryEntry member = new DirectoryEntry(groupMember);\n            output.RenderBeginTag("p");\n            output.Write(member.Name.ToString());\n            output.RenderBeginTag("p");\n        }\n    }\n}