using System.ServiceModel;\nusing QuickFix;\n\n// Create a channel factory\nChannelFactory<IFixService> factory = new ChannelFactory<IFixService>("tcpBinding");\n\n// Create a channel\nIFixService channel = factory.CreateChannel();\n\n// Create a FIX message\nMessage message = new Message();\nmessage.Header.SetField(new MsgType("D"));\nmessage.SetField(new ClOrdID("1"));\n\n// Send the message\nchannel.SendMessage(message.ToString());