[TestMethod]\npublic void SystemUnavailable_TriggersEventAfterBufferPeriod()\n{\n    var mockTimer = new Mock<ITimer>();\n    var monitor = new SystemAvailabilityMonitor(mockTimer.Object, TimeSpan.FromMinutes(10));\n\n    // Setup mock timer to invoke Tick event immediately\n    mockTimer.Setup(t => t.Start(It.IsAny<TimeSpan>())).Raises(t => t.Tick += null, EventArgs.Empty);\n\n    monitor.SystemUnavailable();\n\n    // Assert that SystemBecameUnavailable was raised\n}