using Microsoft.WindowsMobile.PocketOutlook;\n\n// Create a new instance of the SmsAccount class\nSmsAccount smsAccount = new SmsAccount();\n\n// Connect to the device\nsmsAccount.Connect();\n\n// Get the SMS messages\nSmsMessageCollection messages = smsAccount.Messages;\n\n// Iterate through the messages\nforeach (SmsMessage message in messages)\n{\n    // Process the message\n    Console.WriteLine(message.Body);\n}\n\n// Disconnect from the device\nsmsAccount.Disconnect();