public class MyClass\n{\n    private NotThreadSafeObject _notThreadSafeObject;\n\n    public MyClass()\n    {\n        _notThreadSafeObject = new NotThreadSafeObject();\n    }\n\n    public void DoWork()\n    {\n        ThreadPool.QueueUserWorkItem(state =>\n        {\n            // This thread does not access _notThreadSafeObject, so it's safe\n            DoThreadSafeWork();\n        });\n    }\n\n    private void DoThreadSafeWork()\n    {\n        // Perform some thread-safe work here\n    }\n\n    public void DoUnsafeWork()\n    {\n        ThreadPool.QueueUserWorkItem(state =>\n        {\n            // This thread accesses _notThreadSafeObject, making it unsafe\n            _notThreadSafeObject.DoSomething();\n        });\n    }\n}