using System.Windows.Forms;\n\nclass HelloWorld : Form {\n    public HelloWorld() {\n        Text = "Hello World";\n        Label label = new Label { Text = "Hello, world!" };\n        Controls.Add(label);\n    }\n\n    [STAThread]\n    static void Main() {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new HelloWorld());\n    }\n}