using System.IO;\nusing System.Reflection;\n\npublic static string GetSolutionDirectory()\n{\n    string assemblyPath = Assembly.GetExecutingAssembly().Location;\n    string solutionPath = Path.GetDirectoryName(assemblyPath);\n\n    while (!Directory.GetFiles(solutionPath, "*.sln").Any())\n    {\n        solutionPath = Path.GetDirectoryName(solutionPath);\n        if (string.IsNullOrEmpty(solutionPath))\n        {\n            throw new InvalidOperationException("Solution directory not found.");\n        }\n    }\n\n    return solutionPath;\n}