using System.Data.SQLite;\nusing System.Linq;\n\n// create a connection\nusing (SQLiteConnection conn = new SQLiteConnection("Data Source=mydatabase.db"))\n{\n    conn.Open();\n\n    // create a table\n    using (SQLiteCommand cmd = new SQLiteCommand("CREATE TABLE mytable (id INTEGER PRIMARY KEY, name TEXT)", conn))\n    {\n        cmd.ExecuteNonQuery();\n    }\n\n    // insert data\n    using (SQLiteCommand cmd = new SQLiteCommand("INSERT INTO mytable (name) VALUES (@name)", conn))\n    {\n        cmd.Parameters.AddWithValue("@name", "John Doe");\n        cmd.ExecuteNonQuery();\n    }\n\n    // query data using LINQ\n    using (SQLiteDataAdapter adapter = new SQLiteDataAdapter("SELECT * FROM mytable", conn))\n    {\n        DataTable table = new DataTable();\n        adapter.Fill(table);\n\n        var results = table.AsEnumerable()\n            .Where(row => row.Field<string>("name") == "John Doe")\n            .Select(row => new { id = row.Field<int>("id"), name = row.Field<string>("name") })\n            .ToList();\n    }\n}