// Set the cookie upon successful login\nprotected void LoginButton_Click(object sender, EventArgs e)\n{\n    // Validate user credentials against the database\n    if (ValidateCredentials(username, password))\n    {\n        // Generate a token or session ID\n        string token = GenerateToken();\n\n        // Create a secure cookie\n        HttpCookie cookie = new HttpCookie("AuthToken", token);\n        cookie.HttpOnly = true;\n        cookie.Secure = true;\n\n        // Add the cookie to the response\n        Response.Cookies.Add(cookie);\n    }\n}\n\n// Check if the user is logged in on subsequent requests\nprotected void Page_Load(object sender, EventArgs e)\n{\n    // Check if the cookie exists and is valid\n    if (Request.Cookies["AuthToken"] != null)\n    {\n        string token = Request.Cookies["AuthToken"].Value;\n\n        // Validate the token against your database or session store\n        if (ValidateToken(token))\n        {\n            // User is logged in\n        }\n        else\n        {\n            // Token is invalid, delete the cookie\n            Response.Cookies["AuthToken"].Expires = DateTime.Now.AddDays(-1);\n        }\n    }\n    else\n    {\n        // User is not logged in\n    }\n}