using System;\nusing System.Security.Cryptography.X509Certificates;\nusing System.ServiceModel;\nusing System.ServiceModel.Channels;\nusing System.ServiceModel.Security;\n\n// ...\n\n// Create a new SOAP request\nvar request = new SoapRequest();\n\n// Create a new WS-Security header\nvar securityHeader = new SecurityHeader();\n\n// Add the certificate to the security header\nsecurityHeader.Certificates.Add(new X509Certificate2("path/to/certificate.pfx", "password"));\n\n// Add the security header to the SOAP request\nrequest.Headers.Add(securityHeader);\n\n// Send the SOAP request\nvar client = new SoapClient();\nclient.SendRequest(request);