using System.Reflection;\nusing System.Runtime.InteropServices;\n\n// Load the assembly\nAssembly assembly = Assembly.LoadFrom("path/to/assembly.dll");\n\n// Get all types in the assembly\nType[] types = assembly.GetTypes();\n\n// Iterate over the types\nforeach (Type type in types)\n{\n    // Check if the type is COM-visible\n    if (type.IsComVisible())\n    {\n        // Get the ProgID attribute\n        ProgIdAttribute progIdAttr = (ProgIdAttribute)type.GetCustomAttribute(typeof(ProgIdAttribute));\n        if (progIdAttr != null)\n        {\n            string progId = progIdAttr.Value;\n            Console.WriteLine($"Found COM-visible class: {type.Name} with ProgID: {progId}");\n        }\n    }\n}