using WatiN.Core;\n\npublic class MyControlTest\n{\n    [Test]\n    public void TestMyControl()\n    {\n        // Create an instance of Internet Explorer\n        IE ie = new IE("http://localhost/myapp");\n\n        // Find the control on the page\n        var myControl = ie.Element("myControlId");\n\n        // Perform an action on the control\n        myControl.Click();\n\n        // Assert the expected result\n        Assert.IsTrue(ie.Element("resultElementId").Text == "Expected result");\n    }\n}