// Example of using ActiveSync in C#\nusing System;\nusing System.Runtime.InteropServices;\n\nnamespace ActiveSyncExample\n{\n    class Program\n    {\n        [DllImport("rapi.dll")]\n        static extern int RAPIInit();\n\n        [DllImport("rapi.dll")]\n        static extern int RAPISync();\n\n        static void Main(string[] args)\n        {\n            RAPIInit();\n            RAPISync();\n        }\n    }\n}