using System;\nusing System.Diagnostics;\nusing System.Timers;\n\npublic class ProcessMonitor\n{\n    private Timer _timer;\n    private string _processName;\n\n    public ProcessMonitor(string processName, int interval)\n    {\n        _processName = processName;\n        _timer = new Timer(interval);\n        _timer.Elapsed += new ElapsedEventHandler(CheckProcess);\n    }\n\n    private void CheckProcess(object source, ElapsedEventArgs e)\n    {\n        if (IsProcessRunning(_processName))\n        {\n            // Process is running, continue with initializing the rest of the program\n            Console.WriteLine("Process is running");\n            // Add your initialization code here\n        }\n        else\n        {\n            Console.WriteLine("Process is not running");\n        }\n    }\n\n    private bool IsProcessRunning(string processName)\n    {\n        return Process.GetProcessesByName(processName).Length > 0;\n    }\n\n    public void StartMonitoring()\n    {\n        _timer.Start();\n    }\n\n    public void StopMonitoring()\n    {\n        _timer.Stop();\n    }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        ProcessMonitor monitor = new ProcessMonitor("notepad", 10000); // Check every 10 seconds\n        monitor.StartMonitoring();\n        Console.ReadLine();\n    }\n}