// Using resources\nstring[] emailAddresses = My.Resources.EmailAddresses.Split(';');\n\n// Using App.config\nstring[] emailAddresses = ConfigurationManager.AppSettings["EmailAddresses"].Split(';');\n\n// Using a database (example with ADO.NET)\nstring[] emailAddresses = null;\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    SqlCommand command = new SqlCommand("SELECT EmailAddress FROM EmailAddresses", connection);\n    SqlDataReader reader = command.ExecuteReader();\n    while (reader.Read())\n    {\n        emailAddresses = (emailAddresses ?? new string[0]).Concat(new[] { reader["EmailAddress"].ToString() }).ToArray();\n    }\n}