using System.Data.SQLite;\n\n// Create a connection to the database\nusing (SQLiteConnection conn = new SQLiteConnection("Data Source=example.db"))\n{\n    conn.Open();\n\n    // Create a table\n    using (SQLiteCommand cmd = new SQLiteCommand(conn))\n    {\n        cmd.CommandText = "CREATE TABLE IF NOT EXISTS mp3s (id INTEGER PRIMARY KEY, title TEXT, artist TEXT)";\n        cmd.ExecuteNonQuery();\n    }\n\n    // Insert some data\n    using (SQLiteCommand cmd = new SQLiteCommand(conn))\n    {\n        cmd.CommandText = "INSERT INTO mp3s (title, artist) VALUES (@title, @artist)";\n        cmd.Parameters.AddWithValue("@title", "Song Title");\n        cmd.Parameters.AddWithValue("@artist", "Artist Name");\n        cmd.ExecuteNonQuery();\n    }\n}