using System;\nusing System.DirectoryServices;\n\nclass Program\n{\n    static void Main()\n    {\n        string domainName = "yourdomain.com"; // Your domain name\n        string userName = "yourusername";     // Username you want to query\n        string password = "yourpassword";     // Password to use for the query\n        string ldapPath = $"LDAP://{domainName}/CN={userName},CN=Users,DC={domainName},DC=com";\n\n        try\n        {\n            DirectoryEntry entry = new DirectoryEntry(ldapPath, userName, password, AuthenticationTypes.Secure);\n            DirectorySearcher searcher = new DirectorySearcher(entry);\n\n            // You need to set the Filter and PropertiesToLoad to actually load what you want\n            searcher.Filter = $"(sAMAccountName={userName})";\n            searcher.PropertiesToLoad.Add("TerminalServicesHomeDrive");\n\n            SearchResult result = searcher.FindOne();\n\n            if (result != null)\n            {\n                Console.WriteLine($"TerminalServicesHomeDrive: {result.Properties["TerminalServicesHomeDrive"][0]}");\n            }\n            else\n            {\n                Console.WriteLine("User not found.");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}