using WebSocketSharp;\n\n// Create a WebSocket server\nvar wssv = new WebSocketServer("ws://localhost:8080");\n\n// Handle incoming connections\nwssv.OnOpen += (sender, e) => {\n    Console.WriteLine("Client connected");\n};\n\n// Handle incoming messages\nwssv.OnMessage += (sender, e) => {\n    Console.WriteLine("Received message: " + e.Data);\n};\n\n// Handle price updates\npublic void UpdatePrice(string productId, string vendorId, decimal price) {\n    // Broadcast the update to all connected clients\n    wssv.Broadcast($"update|{productId}|{vendorId}|{price}");\n}\n\n// Start the WebSocket server\nwssv.Start();