<asp:CustomValidator id="compval" runat="server" \n                     ErrorMessage="The values are not equal." \n                     ControlToValidate="txtBox1" \n                     OnServerValidate="ValidateFloatComparison">*</asp:CustomValidator>\n\n// Server-side validation function\nprotected void ValidateFloatComparison(object source, ServerValidateEventArgs args)\n{\n    string value1 = txtBox1.Text;\n    string value2 = txtBox2.Text;\n\n    float float1, float2;\n\n    // Try to parse the input values as floats\n    if (float.TryParse(value1, out float1) && float.TryParse(value2, out float2))\n    {\n        // Compare the float values\n        args.IsValid = float1 == float2;\n    }\n    else\n    {\n        // If either value cannot be parsed as a float, the validation fails\n        args.IsValid = false;\n    }\n}