using System;\nusing System.Data;\nusing System.Xml;\n\npublic class XmlDocumentToDataTable\n{\n    public static DataTable ConvertXmlDocumentToDataTable(XmlDocument xmlAPDP)\n    {\n        DataSet dsAPDP = new DataSet();\n        // Use the XmlDocument to create an XmlNodeReader\n        XmlNodeReader reader = new XmlNodeReader(xmlAPDP);\n        // Load the DataSet using the XmlNodeReader\n        dsAPDP.ReadXml(reader);\n        // Assuming the first table in the DataSet is the one you want\n        DataTable dtAPDP = dsAPDP.Tables[0];\n        return dtAPDP;\n    }\n}