// Example using a delegate instead of Template Method Pattern\npublic delegate void AlgorithmStep();\n\npublic class Algorithm\n{\n    public void Run(AlgorithmStep step1, AlgorithmStep step2)\n    {\n        step1();\n        step2();\n    }\n}\n\npublic class ConcreteAlgorithm\n{\n    public void Step1() { Console.WriteLine("Step 1"); }\n    public void Step2() { Console.WriteLine("Step 2"); }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var algorithm = new Algorithm();\n        var concreteAlgorithm = new ConcreteAlgorithm();\n\n        algorithm.Run(concreteAlgorithm.Step1, concreteAlgorithm.Step2);\n    }\n}