using Oracle.ManagedDataAccess.Client;\nusing System.Data;\n\n// Create a connection and command\nOracleConnection conn = new OracleConnection("your connection string");\nOracleCommand cmd = new OracleCommand("GetCompanyList", conn);\ncmd.CommandType = CommandType.StoredProcedure;\n\n// Fetch the data\nOracleDataReader reader = cmd.ExecuteReader();\n\n// Create a DataTable to store the results\nDataTable companies = new DataTable();\ncompanies.Columns.Add("CompanyID", typeof(int));\ncompanies.Columns.Add("CompanyName", typeof(string));\ncompanies.Columns.Add("OrderRecords", typeof(DataTable));\n\nwhile (reader.Read())\n{\n    int companyId = reader.GetInt32(0);\n    string companyName = reader.GetString(1);\n    OracleUdtValue orderRecords = reader.GetOracleUdtValue(2);\n    DataTable orders = new DataTable();\n    orders.Columns.Add("OrderID", typeof(int));\n    orders.Columns.Add("OrderDate", typeof(DateTime));\n    orders.Columns.Add("Total", typeof(decimal));\n\n    foreach (OracleUdtValue orderRecord in orderRecords.GetValues())\n    {\n        orders.Rows.Add(orderRecord.GetValues());\n    }\n\n    companies.Rows.Add(companyId, companyName, orders);\n}\n\n// Close the reader and connection\nreader.Close();\nconn.Close();