using RazorEngine;\nusing RazorEngine.Templating;\n\n// Define the template\nstring template = @"\n<html>\n  <body>\n    <h1>Heading Here</h1>\n    Dear @Model.UserName,\n    <br />\n    <p>First part of the email body goes here</p>\n  </body>\n</html>";\n\n// Create a model\npublic class EmailModel\n{\n  public string UserName { get; set; }\n}\n\n// Create an instance of the model\nEmailModel model = new EmailModel { UserName = "John Doe" };\n\n// Compile and render the template\nstring mailBody = Engine.Razor.RunCompile(template, "emailTemplateKey", typeof(EmailModel), model);\n\n// Use the rendered template as the email body