X509Certificate2Collection certificates = new X509Certificate2Collection();\n\n// add certificates to the collection\ncertificates.Add(new X509Certificate2("path/to/certificate1.pfx", "password"));\ncertificates.Add(new X509Certificate2("path/to/certificate2.pfx", "password"));\n// ...\n\nX509Store store = new X509Store(StoreName.My, StoreLocation.CurrentUser);\nstore.Open(OpenFlags.ReadWrite);\nstore.AddRange(certificates);\nstore.Close();