using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    string insertString = @"INSERT INTO upload_history (field1, field2, field3) \n                            VALUES (@field1, @field2, @field3)";\n\n    SqlCommand command = new SqlCommand(insertString, connection);\n\n    command.Parameters.AddWithValue("@field1", varField1 ?? (object)DBNull.Value);\n    command.Parameters.AddWithValue("@field2", varField2 ?? (object)DBNull.Value);\n    command.Parameters.AddWithValue("@field3", varField3 ?? (object)DBNull.Value);\n\n    command.ExecuteNonQuery();\n}