using System;\nusing System.Runtime.InteropServices;\n\nnamespace PortableDeviceAccess\n{\n    class Program\n    {\n        [DllImport("wpdapi.dll")]\n        private static extern IntPtr WPD_Initialize();\n\n        [DllImport("wpdapi.dll")]\n        private static extern IntPtr WPD_OpenDevice(IntPtr hWpd, string deviceId);\n\n        [DllImport("wpdapi.dll")]\n        private static extern void WPD_CloseDevice(IntPtr hDevice);\n\n        static void Main(string[] args)\n        {\n            IntPtr hWpd = WPD_Initialize();\n            if (hWpd != IntPtr.Zero)\n            {\n                IntPtr hDevice = WPD_OpenDevice(hWpd, "Q1/Datacasts");\n                if (hDevice != IntPtr.Zero)\n                {\n                    // Use the hDevice handle to access the device's contents\n                    // ...\n                    WPD_CloseDevice(hDevice);\n                }\n                // Release WPD resources\n                WPD_Close(hWpd);\n            }\n        }\n    }\n}