using Socks4;\n\n// Create a new Socks4Proxy instance\nSocks4Proxy proxy = new Socks4Proxy("your-proxy-server.com", 1080);\n\n// Set the proxy credentials\nproxy.Username = "your-username";\nproxy.Password = "your-password";\n\n// Create a new TcpClient instance\nTcpClient client = new TcpClient();\n\n// Connect to the proxy\nproxy.Connect(client, "www.example.com", 80);\n\n// Get the response\nNetworkStream stream = client.GetStream();