using System;\nusing System.IO;\nusing System.IO.Compression;\nusing System.Web;\n\npublic class ZipFileGenerator\n{\n    public void GenerateAndSendZip(HttpResponse response)\n    {\n        // Create a memory stream to store the zip file\n        using (var ms = new MemoryStream())\n        {\n            // Create a zip archive\n            using (var zipArchive = new ZipArchive(ms, ZipArchiveMode.Create, true))\n            {\n                // Add files to the zip archive\n                var entry = zipArchive.CreateEntry("file1.txt");\n                using (var writer = new StreamWriter(entry.Open()))\n                {\n                    writer.WriteLine("Hello, World!");\n                }\n\n                // Add more files as needed...\n            }\n\n            // Set the response headers\n            response.Clear();\n            response.ContentType = "application/zip";\n            response.AppendHeader("Content-Disposition", "attachment; filename=example.zip");\n\n            // Send the zip file to the user\n            ms.Position = 0;\n            ms.CopyTo(response.OutputStream);\n            response.End();\n        }\n    }\n}