// Factory class for creating ADIF writers\nnamespace ADIF.Common\n{\n    public class ADIFWriterFactory\n    {\n        public static IADIFWriter CreateWriter(string version)\n        {\n            switch (version)\n            {\n                case "1.0":\n                    return new Version1.ADIFWriter();\n                case "2.2.2":\n                    return new Version222.ADIFWriter();\n                // Add more versions as needed\n                default:\n                    throw new ArgumentException("Unsupported version");\n            }\n        }\n    }\n}