using System;\nusing System.Data;\nusing System.Data.OleDb;\n\nclass WDSQueryExample\n{\n    static void Main(string[] args)\n    {\n        string query = "SELECT System.ItemName, System.ItemUrl, System.Search.Rank, System.Search.HitHighlight " +\n                       "FROM SystemIndex " +\n                       "WHERE CONTAINS('search term')";\n\n        OleDbConnection conn = new OleDbConnection("Provider=Search.CollatorDSO;Extended Properties='Application=Windows'");\n        OleDbCommand cmd = new OleDbCommand(query, conn);\n        OleDbDataAdapter da = new OleDbDataAdapter(cmd);\n\n        DataTable results = new DataTable();\n        da.Fill(results);\n\n        foreach (DataRow row in results.Rows)\n        {\n            Console.WriteLine("Item Name: " + row["System.ItemName"]);\n            Console.WriteLine("Item URL: " + row["System.ItemUrl"]);\n            Console.WriteLine("Rank: " + row["System.Search.Rank"]);\n            Console.WriteLine("Hit Highlight: " + row["System.Search.HitHighlight"]);\n        }\n    }\n}