using System.Reflection;\n\n// Get the MethodInfo for a method\nMethodInfo method = typeof(MyClass).GetMethod("MyMethod");\n\n// Get the string representation of the method\nstring methodString = method.ToString();\n\n// Output: "Void MyMethod(System.String, System.Int32)"\n\n// Get the FieldInfo for a field\nFieldInfo field = typeof(MyClass).GetField("MyField");\n\n// Get the string representation of the field\nstring fieldString = field.ToString();\n\n// Output: "System.String MyField"