using Oracle.DataAccess.Client;\n\n// Create a connection to the Oracle database\nusing (OracleConnection conn = new OracleConnection("Data Source=your_database;User ID=your_username;Password=your_password;"))\n{\n    conn.Open();\n\n    // Create a bulk copy object\n    OracleBulkCopy bulkCopy = new OracleBulkCopy(conn);\n\n    // Set the destination table\n    bulkCopy.DestinationTableName = "your_table_name";\n\n    // Read data from the TXT file\n    using (StreamReader reader = new StreamReader("your_file_path.txt"))\n    {\n        while (!reader.EndOfStream)\n        {\n            string[] columns = reader.ReadLine().Split(',');\n            bulkCopy.AddRow(columns);\n        }\n    }\n\n    // Perform the bulk copy\n    bulkCopy.WriteToServer();\n}