// Set the Forms Authentication ticket\nFormsAuthenticationTicket ticket = new FormsAuthenticationTicket(1, // version\n    UserName, // username\n    DateTime.Now, // creation time\n    DateTime.Now.AddMinutes(30), // expiration time\n    false, // is persistent\n    userid); // user data\n\n// Encrypt the ticket\nstring encryptedTicket = FormsAuthentication.Encrypt(ticket);\n\n// Store the encrypted ticket in a cookie\nResponse.Cookies.Add(new HttpCookie(FormsAuthentication.FormsCookieName, encryptedTicket));