using Renci.SshNet;\n\n// Create an SSH client instance\nvar sshClient = new SshClient("linux-server-ip", "username", "password");\n\n// Connect to the Linux server\nsshClient.Connect();\n\n// Execute the optimizer and receive stdout\nvar command = sshClient.RunCommand("java -jar optimizer.jar");\nvar output = command.Result;\n\n// Display the output on the UI\nConsole.WriteLine(output);\n\n// Disconnect from the Linux server\nsshClient.Disconnect();