using System.Diagnostics;\n\npublic class Watchdog\n{\n    private Process jabberClientProcess;\n\n    public Watchdog()\n    {\n        jabberClientProcess = new Process();\n        jabberClientProcess.StartInfo.FileName = "YourJabberClient.exe";\n        jabberClientProcess.Start();\n    }\n\n    private void MonitorProcess()\n    {\n        while (true)\n        {\n            if (jabberClientProcess.HasExited)\n            {\n                jabberClientProcess.Start();\n            }\n            Thread.Sleep(1000); // Monitor every 1 second\n        }\n    }\n}