using System;\nusing System.Net;\nusing System.Xml;\n\nnamespace FlickrImageDownloader\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string apiKey = "YOUR_FLICKR_API_KEY";\n            string keyword = "nature"; // Replace with your desired keyword\n            string url = $"https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key={apiKey}&tags={keyword}&format=rest";\n\n            // Send request to Flickr API\n            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n            HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n\n            // Parse XML response\n            XmlDocument xmlDoc = new XmlDocument();\n            xmlDoc.Load(response.GetResponseStream());\n            XmlNodeList photos = xmlDoc.GetElementsByTagName("photo");\n\n            // Select a random photo\n            Random rand = new Random();\n            int index = rand.Next(0, photos.Count);\n            XmlNode photo = photos[index];\n\n            // Get photo URL\n            string photoUrl = $"https://farm{photo.Attributes["farm"].Value}.staticflickr.com/{photo.Attributes["server"].Value}/{photo.Attributes["id"].Value}_{photo.Attributes["secret"].Value}.jpg";\n\n            // Download photo\n            using (WebClient client = new WebClient())\n            {\n                client.DownloadFile(photoUrl, "random_image.jpg");\n            }\n\n            Console.WriteLine("Image downloaded successfully!");\n        }\n    }\n}