// Create a unique cache key based on the search query\nstring cacheKey = $"SearchResults_{searchQuery}";\n\n// Check if the cache entry exists\nif (Cache[cacheKey] != null)\n{\n    // Retrieve the cached datatable\n    DataTable dataTable = (DataTable)Cache[cacheKey];\n}\nelse\n{\n    // Perform the search query and store the results in Cache\n    DataTable dataTable = PerformSearchQuery(searchQuery);\n    Cache.Add(cacheKey, dataTable, null, DateTime.Now.AddHours(1), Cache.NoSlidingExpiration);\n}