protected void Page_Load(object sender, EventArgs e)\n{\n    Response.Clear();\n    Response.ContentType = "text/xml";\n    Response.Write("<?xml version='1.0' encoding='utf-8'?>");\n    Response.Write("<rows>");\n    Response.Write("<page>1</page>");\n    Response.Write("<total>25</total>");\n    Response.Write("<records>3</records>");\n    for (int i = 1; i <= 3; i++)\n    {\n        Response.Write("<row id='" + i + "'>");\n        Response.Write("<cell>Row " + i + ", Column 1</cell>");\n        Response.Write("<cell>Row " + i + ", Column 2</cell>");\n        Response.Write("<cell>Row " + i + ", Column 3</cell>");\n        Response.Write("</row>");\n    }\n    Response.Write("</rows>");\n    Response.End();\n}