using System;\nusing System.IO;\n\npublic class FileTypeVerifier\n{\n    private static readonly Dictionary<string, byte[]> _fileSignatures = new Dictionary<string, byte[]>\n    {\n        { ".pdf", new byte[] { 0x25, 0x50, 0x44, 0x46 } },\n        { ".xls", new byte[] { 0xd0, 0xcf, 0x11, 0xe0, 0xa1, 0xb1, 0x1a, 0xe1 } },\n        // Add more signatures as needed\n    };\n\n    public bool VerifyFileType(string filePath, string expectedExtension)\n    {\n        if (!_fileSignatures.TryGetValue(expectedExtension.ToLower(), out byte[] signature))\n        {\n            throw new ArgumentException("Unsupported file extension");\n        }\n\n        using (var fileStream = new FileStream(filePath, FileMode.Open, FileAccess.Read))\n        {\n            var fileSignature = new byte[signature.Length];\n            fileStream.Read(fileSignature, 0, signature.Length);\n\n            return fileSignature.SequenceEqual(signature);\n        }\n    }\n}