using System;\nusing System.Collections.Generic;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        const int count = 6000000; // 6 million\n        int[] array = new int[count];\n        List<int> list = new List<int>(count);\n\n        // Populate the array and list\n        for (int i = 0; i < count; i++)\n        {\n            array[i] = i;\n            list.Add(i);\n        }\n\n        // Benchmark array iteration\n        Stopwatch stopwatch = Stopwatch.StartNew();\n        int sumArray = 0;\n        for (int i = 0; i < count; i++)\n        {\n            sumArray += array[i];\n        }\n        stopwatch.Stop();\n        Console.WriteLine($"Array iteration: {stopwatch.ElapsedMilliseconds}ms");\n\n        // Benchmark list iteration\n        stopwatch.Restart();\n        int sumList = 0;\n        for (int i = 0; i < count; i++)\n        {\n            sumList += list[i];\n        }\n        stopwatch.Stop();\n        Console.WriteLine($"List iteration: {stopwatch.ElapsedMilliseconds}ms");\n    }\n}