// Part1Model.cs\npublic class Part1Model\n{\n    public string Data { get; set; }\n}\n\n// Part1Controller.cs\npublic class Part1Controller : Controller\n{\n    private readonly IPart1DataService _dataService;\n\n    public Part1Controller(IPart1DataService dataService)\n    {\n        _dataService = dataService;\n    }\n\n    public ActionResult Index()\n    {\n        var model = new Part1Model { Data = _dataService.GetData() };\n        return PartialView("_Part1", model);\n    }\n}