using System;\nusing System.IO;\n\npublic class DirectoryTraversal\n{\n    public static void Traverse(string rootDirectory)\n    {\n        // Create a DirectoryInfo object for the root directory\n        var root = new DirectoryInfo(rootDirectory);\n\n        // Iterate through the directory hierarchy\n        foreach (var file in root.EnumerateFiles("*", SearchOption.AllDirectories))\n        {\n            // Check if the file is a junction point\n            if (IsJunctionPoint(file.FullName))\n            {\n                Console.WriteLine($"Skipping junction point: {file.FullName}");\n                continue;\n            }\n\n            // Process the file\n            Console.WriteLine($"Processing file: {file.FullName}");\n        }\n    }\n\n    private static bool IsJunctionPoint(string filePath)\n    {\n        // Use the `File.GetAttributes` method to check for the reparse point attribute\n        var attributes = File.GetAttributes(filePath);\n        return attributes.HasFlag(FileAttributes.ReparsePoint);\n    }\n}