using (var context = new NorthwindContext())\n{\n    var customers = context.Customers.ToList(); // Assuming you have customers already\n    var items = context.Items.ToList(); // Assuming you have items already\n\n    // Creating a new order for example\n    var newOrder = new Order\n    {\n        CustomerId = customers.FirstOrDefault().CustomerId, // Example customer\n        OrderDate = DateTime.Now,\n        OrderDetails = new List<OrderDetail>\n        {\n            new OrderDetail\n            {\n                ItemId = items.FirstOrDefault().ItemId, // Example item\n                Quantity = 2\n            }\n        }\n    };\n\n    // Adding the new order to the context\n    context.Orders.Add(newOrder);\n\n    // Saving changes to the database\n    context.SaveChanges();\n}