using System.IO.IsolatedStorage;\nusing System.Security.Cryptography;\n\n// Create an encrypted isolated storage\nIsolatedStorageFile isoStore = IsolatedStorageFile.GetUserStoreForApplication();\nisoStore.CreateDirectory("SecureData");\n\n// Encrypt and store sensitive data\nstring sensitiveData = "my_sensitive_data";\nbyte[] encryptedData = ProtectedData.Protect(Encoding.UTF8.GetBytes(sensitiveData), null);\nisoStore.WriteFile("SecureData\\my_data.txt", encryptedData);