using System;\nusing System.Xml;\nusing System.Xml.XPath;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string xml = @"\n<markup>\n    <element1 attributea=""blah"">@@value1@@</element1>\n    <element2>@@value2@@</element2>\n    <element3>\n        <element3point1>@@value1@@</element3point1>\n        <element3point2>@@value3@@</element3point2>\n        <element3point3>apple</element3point3>\n    </element3>\n    <element4>pear</element4>\n</markup>";\n\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xml);\n\n        XPathNavigator navigator = doc.CreateNavigator();\n        XPathNodeIterator iterator = navigator.Select("//text()[contains(., '@@')]");\n        while (iterator.MoveNext())\n        {\n            string text = iterator.Current.Value;\n            Console.WriteLine(text);\n        }\n    }\n}